mapper.select:
SELECT * FROM punish where 1=1
and username like #{content}
and bar_code in (select bar_code from bookitem where reference_num in (select reference_num from book where book_name like #{content}))
and bar_code in (select bar_code from bookitem where reference_num in (select reference_num from book where isbn like #{content}))
order by FIELD(`is_handle`,0,1),update_time DESC limit #{start},#{each_num}

SELECT * FROM punish where 1=1
order by FIELD(`is_handle`,0,1),update_time DESC
limit #{start},#{each_num}

SELECT * FROM punish,bookitem,book where username=#{username} and punish.bar_code=bookitem.bar_code and bookitem.reference_num=book.reference_num
order by FIELD(`is_handle`,0,1),punish.update_time DESC
limit #{start},#{each_num}
